import React, { useState, ChangeEvent } from "react";
import { ITask } from "./interfaces";

interface Props {
  task: ITask;
  deleteTask(taskNameToDelete: string): void;
  editing: boolean;
  toggleEdit(): void;
  updateTask(updatedTask: ITask): void;
}

const TodoTask: React.FC<Props> = ({
  task,
  deleteTask,
  editing,
  toggleEdit,
  updateTask,
}) => {
  const [editedTask, setEditedTask] = useState<ITask>({ ...task });

  const handleChange = (event: ChangeEvent<HTMLInputElement>): void => {
    const { name, value } = event.target;
    setEditedTask((prevTask) => ({
      ...prevTask,
      [name]: value,
    }));
  };

  const handleSave = (): void => {
    updateTask(editedTask);
    toggleEdit();
  };

  return (
    <div className="tasks">
      <div className="content">
        {/* STATE ON EDIT */}
        {editing ? (
          <>
            <input
              type="text"
              name="taskName"
              value={editedTask.taskName}
              onChange={handleChange}
            />
            <input
              type="date"
              name="taskDate"
              value={editedTask.taskDate}
              onChange={handleChange}
            />
            <input
              type="date"
              name="deadline"
              value={editedTask.deadline}
              onChange={handleChange}
            />
            <button onClick={handleSave}>Save</button>
            <button
              className="deleteButton"
              onClick={() => deleteTask(task.taskName)}
            >
              X
            </button>
          </>
        ) : (
          // NORMAL STATE
          <>
            <span className="todo1">{task.taskName}</span>
            <span className="todo2">{task.taskDate}</span>
            <span className="todo3">{task.deadline}</span>
            <button className="editeButton" onClick={toggleEdit}>
              Edit
            </button>
            <button
              className="deleteButton"
              onClick={() => deleteTask(task.taskName)}
            >
              X
            </button>
          </>
        )}
      </div>
    </div>
  );
};

export default TodoTask;
